mapper.select:
SELECT * FROM hot_products
WHERE recommend_date = #{date}

    AND category_id = #{categoryId}

ORDER BY rank_in_category ASC

SELECT * FROM hot_products
WHERE recommend_date = #{date}
ORDER BY category_id ASC, rank_in_category ASC

SELECT * FROM hot_products
WHERE product_id = #{productId}
  AND recommend_date BETWEEN #{startDate} AND #{endDate}
ORDER BY recommend_date DESC

SELECT * FROM hot_products
WHERE recommend_date = #{date}
  AND category_id = #{categoryId}
ORDER BY rank_in_category ASC
LIMIT #{topN}

SELECT COUNT(*) FROM hot_products
WHERE recommend_date = #{date}
  AND category_id = #{categoryId}

mapper.delete:
DELETE FROM hot_products
WHERE recommend_date = #{date}
  AND category_id = #{categoryId}
